package Week1Day1;

public class PrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=250;
		if(i%2==0)
		{
			System.out.println(i + "is not a prime");
		}
		else
		{
			System.out.println(i + "is a prime");
		}
				
				

	}

}
